import React from 'react';
import styled from 'styled-components';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 0 auto;
`;

const WheelContent = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  background-image: url('/inocta-trace.svg');
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
`;

const InteractiveArea = styled.div`
  position: absolute;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: white;
  transition: all 0.3s ease;
  z-index: 2;

  &:hover {
    transform: scale(1.05);
  }

  .icon {
    margin-bottom: 8px;
    width: 40px;
    height: 40px;

    svg {
      width: 100%;
      height: 100%;
      fill: white;
    }
  }

  .label {
    font-size: 14px;
    text-align: center;
  }

  &.top {
    top: 40px;
    left: 50%;
    transform: translateX(-50%);
    width: 120px;
    height: 140px;
  }

  &.right {
    right: 40px;
    top: 50%;
    transform: translateY(-50%);
    width: 140px;
    height: 120px;
  }

  &.bottom {
    bottom: 40px;
    left: 50%;
    transform: translateX(-50%);
    width: 120px;
    height: 140px;
  }

  &.left {
    left: 40px;
    top: 50%;
    transform: translateY(-50%);
    width: 140px;
    height: 120px;
  }
`;

const CenterContent = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  cursor: pointer;
  z-index: 2;
  transition: all 0.3s ease;

  &:hover {
    transform: translate(-50%, -50%) scale(1.05);
  }

  .icon {
    margin-bottom: 8px;
    width: 50px;
    height: 50px;

    svg {
      width: 100%;
      height: 100%;
      fill: #E0A967;
    }
  }

  h3 {
    font-size: 14px;
    margin: 0;
    color: #333;
  }
`;

const sections = [
  { 
    id: 'teams', 
    position: 'top',
    label: 'Teams',
    icon: '/teams.svg'
  },
  { 
    id: 'resources', 
    position: 'right',
    label: 'Resources',
    icon: '/ressources.svg'
  },
  { 
    id: 'execution', 
    position: 'bottom',
    label: 'Execution',
    icon: '/execution.svg'
  },
  { 
    id: 'actions', 
    position: 'left',
    label: 'Actions',
    icon: '/actions.svg'
  }
];

const centerSection = {
  id: 'compliance',
  label: 'Compliance & Alignment',
  icon: '/compliance.svg'
};

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <WheelContent>
        <CenterContent onClick={() => onComponentSelect(centerSection)}>
          <div className="icon">
            <img src={centerSection.icon} alt="Compliance" />
          </div>
          <h3>{centerSection.label}</h3>
        </CenterContent>

        {sections.map((section) => (
          <InteractiveArea
            key={section.id}
            className={section.position}
            onClick={() => onComponentSelect(section)}
          >
            <div className="icon">
              <img src={section.icon} alt={section.label} />
            </div>
            <div className="label">{section.label}</div>
          </InteractiveArea>
        ))}
      </WheelContent>
    </WheelContainer>
  );
}

export default FrameworkWheel;
