import React from 'react';
import styled from 'styled-components';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 0 auto;
`;

const WheelContent = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  background-image: url('/inocta2.svg');
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
`;

const InteractiveArea = styled.div`
  position: absolute;
  cursor: pointer;
  opacity: 0;
  transition: opacity 0.3s ease;

  &:hover {
    opacity: 0.1;
    background-color: #000;
  }

  &.top {
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 120px;
    height: 140px;
    clip-path: polygon(20% 0%, 80% 0%, 100% 100%, 0% 100%);
  }

  &.right {
    right: 0;
    top: 50%;
    transform: translateY(-50%);
    width: 140px;
    height: 120px;
    clip-path: polygon(0% 20%, 100% 0%, 100% 100%, 0% 80%);
  }

  &.bottom {
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 120px;
    height: 140px;
    clip-path: polygon(0% 0%, 100% 0%, 80% 100%, 20% 100%);
  }

  &.left {
    left: 0;
    top: 50%;
    transform: translateY(-50%);
    width: 140px;
    height: 120px;
    clip-path: polygon(0% 0%, 100% 20%, 100% 80%, 0% 100%);
  }
`;

const sections = [
  { id: 'teams', position: 'top' },
  { id: 'resources', position: 'right' },
  { id: 'execution', position: 'bottom' },
  { id: 'actions', position: 'left' }
];

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <WheelContent>
        {sections.map((section) => (
          <InteractiveArea
            key={section.id}
            className={section.position}
            onClick={() => onComponentSelect(section)}
          />
        ))}
      </WheelContent>
    </WheelContainer>
  );
}

export default FrameworkWheel;
