import React from 'react';
import styled from 'styled-components';
import { 
  FaUsers, 
  FaDatabase, 
  FaBrain, 
  FaBalanceScale, 
  FaCogs 
} from 'react-icons/fa';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 0 auto;
`;

const WheelContent = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
`;

const CenterContent = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  z-index: 3;
  width: 150px;

  h3 {
    margin: 0;
    font-size: 16px;
    color: #333;
  }

  .icon {
    color: #e9a65b;
    font-size: 24px;
    margin-bottom: 8px;
  }
`;

const StarShape = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 300px;
  height: 300px;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: white;
    clip-path: polygon(
      50% 0%,
      61% 35%,
      98% 35%,
      68% 57%,
      79% 91%,
      50% 70%,
      21% 91%,
      32% 57%,
      2% 35%,
      39% 35%
    );
  }
`;

const ComponentSection = styled.div`
  position: absolute;
  display: flex;
  flex-direction: column;
  align-items: center;
  color: white;

  .icon-container {
    width: 60px;
    height: 60px;
    background-color: ${props => props.color || '#51817c'};
    border-radius: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 8px;
  }

  .label {
    color: ${props => props.textColor || '#51817c'};
    font-size: 14px;
    text-align: center;
    margin-top: 8px;
  }
`;

const ConnectingPart = styled.div`
  position: absolute;
  width: 120px;
  height: 40px;
  background-color: ${props => props.color || '#e9a65b'};
  transform-origin: center;
  z-index: 1;
`;

const components = [
  {
    id: 'teams',
    name: 'Teams',
    icon: FaUsers,
    color: '#51817c',
    position: 'top'
  },
  {
    id: 'resources',
    name: 'Resources',
    icon: FaBrain,
    color: '#51817c',
    position: 'right'
  },
  {
    id: 'execution',
    name: 'Execution',
    icon: FaCogs,
    color: '#51817c',
    position: 'bottom'
  },
  {
    id: 'actions',
    name: 'Actions',
    icon: FaCogs,
    color: '#51817c',
    position: 'left'
  }
];

const getPosition = (position) => {
  const distance = 180;
  switch (position) {
    case 'top':
      return { top: '0', left: '50%', transform: 'translateX(-50%)' };
    case 'right':
      return { top: '50%', right: '0', transform: 'translateY(-50%)' };
    case 'bottom':
      return { bottom: '0', left: '50%', transform: 'translateX(-50%)' };
    case 'left':
      return { top: '50%', left: '0', transform: 'translateY(-50%)' };
    default:
      return {};
  }
};

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <WheelContent>
        <StarShape />
        <CenterContent>
          <FaBalanceScale className="icon" />
          <h3>Compliance & Alignment</h3>
        </CenterContent>
        {components.map((component) => (
          <React.Fragment key={component.id}>
            <ComponentSection
              color={component.color}
              style={getPosition(component.position)}
              onClick={() => onComponentSelect(component)}
            >
              <div className="icon-container">
                <component.icon size={24} />
              </div>
              <div className="label">{component.name}</div>
            </ComponentSection>
          </React.Fragment>
        ))}
      </WheelContent>
    </WheelContainer>
  );
}

export default FrameworkWheel;
