import React from 'react';
import styled from 'styled-components';

const Panel = styled.div`
  padding: 20px;
  border-radius: 10px;
  background-color: #f8f9fa;
  margin-top: 20px;
`;

const ComponentTitle = styled.h3`
  color: ${props => props.color};
  margin-bottom: 15px;
`;

const componentDetails = {
  teams: {
    title: 'Teams & Agents Alignment & Structure',
    description: 'Ensure that your teams are aligned to maximize both human creativity and AI efficiency. In today\'s landscape, AI agents, bots, and robots are no longer external tools but integral members of the team.',
    question: 'Is our team structure set up to fully utilize both human and AI capabilities?'
  },
  resources: {
    title: 'Resources & The Corpus of Knowledge',
    description: 'Organize and curate your company\'s data and knowledge effectively to fuel AI\'s potential. This will enable AI to deliver smarter, more impactful outcomes while engaging and educating customers.',
    question: 'Is our knowledge structured to support AI-driven solutions and customer experiences?'
  },
  actions: {
    title: 'Actions & Orchestrations Planning',
    description: 'Streamline operations and processes to allow seamless collaboration between AI and human-driven initiatives. Establish workflows that automate tasks while maintaining alignment with business goals.',
    question: 'Are our processes optimized to drive AI initiatives efficiently?'
  },
  compliance: {
    title: 'Compliance, Guardrails & Alignment',
    description: 'Implement ethical and regulatory standards into AI systems to ensure alignment with organizational values and societal expectations. Compliance acts as a safeguard for trust and accountability.',
    question: 'Are our AI efforts aligned with ethical standards and industry regulations?'
  },
  execution: {
    title: 'Execution Infrastructure & Tech Architecture',
    description: 'Design a scalable infrastructure that aligns technology with your business goals. Ensure that execution is efficient and adaptable, enabling smooth integration of AI solutions.',
    question: 'How well is our tech infrastructure prepared for AI-driven execution?'
  }
};

function DetailPanel({ selectedComponent }) {
  if (!selectedComponent) {
    return (
      <Panel>
        <h3>Select a component to see details</h3>
      </Panel>
    );
  }

  const details = componentDetails[selectedComponent.id];

  return (
    <Panel>
      <ComponentTitle color={selectedComponent.color}>{details.title}</ComponentTitle>
      <p>{details.description}</p>
      <p><strong>Key Question:</strong> {details.question}</p>
    </Panel>
  );
}

export default DetailPanel;
