import React from 'react';
import styled from 'styled-components';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 0 auto;
`;

const WheelContent = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
`;

const CrossShape = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80%;
  height: 80%;

  &::before,
  &::after {
    content: '';
    position: absolute;
    background-color: #E0A967;
  }

  &::before {
    width: 100%;
    height: 20%;
    top: 40%;
    left: 0;
    transform: rotate(45deg);
  }

  &::after {
    width: 100%;
    height: 20%;
    top: 40%;
    left: 0;
    transform: rotate(-45deg);
  }
`;

const Section = styled.div`
  position: absolute;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 120px;
  text-align: center;
  z-index: 3;

  &.teams {
    top: 0;
    left: 50%;
    transform: translateX(-50%);
  }

  &.resources {
    top: 50%;
    right: 0;
    transform: translateY(-50%);
  }

  &.execution {
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
  }

  &.actions {
    top: 50%;
    left: 0;
    transform: translateY(-50%);
  }

  .section-content {
    background-color: #557B70;
    padding: 15px;
    border-radius: 8px;
    width: 100%;
  }

  .icon {
    width: 40px;
    height: 40px;
    margin: 0 auto 8px;
    filter: brightness(0) invert(1);
  }

  .label {
    color: white;
    font-size: 14px;
    margin: 0;
  }
`;

const CenterContent = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  z-index: 3;
  width: 150px;

  .icon {
    width: 50px;
    height: 50px;
    margin: 0 auto 10px;
    filter: brightness(0) sepia(1) saturate(5) hue-rotate(30deg);
  }

  .label {
    color: #000;
    font-size: 16px;
    font-weight: 500;
    margin: 0;
  }
`;

const sections = [
  {
    id: 'teams',
    className: 'teams',
    label: 'Teams',
    icon: '/teams.svg'
  },
  {
    id: 'resources',
    className: 'resources',
    label: 'Resources',
    icon: '/resources.svg'
  },
  {
    id: 'execution',
    className: 'execution',
    label: 'Execution',
    icon: '/execution.svg'
  },
  {
    id: 'actions',
    className: 'actions',
    label: 'Actions',
    icon: '/actions.svg'
  }
];

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <WheelContent>
        <CrossShape />

        <CenterContent onClick={() => onComponentSelect({
          id: 'compliance',
          label: 'Compliance & Alignment'
        })}>
          <img className="icon" src="/compliance.svg" alt="Compliance" />
          <h3 className="label">Compliance & Alignment</h3>
        </CenterContent>

        {sections.map((section) => (
          <Section 
            key={section.id}
            className={section.className}
            onClick={() => onComponentSelect(section)}
          >
            <div className="section-content">
              <img className="icon" src={section.icon} alt={section.label} />
              <p className="label">{section.label}</p>
            </div>
          </Section>
        ))}
      </WheelContent>
    </WheelContainer>
  );
}

export default FrameworkWheel;
