import React from 'react';
import styled from 'styled-components';
import { 
  FaUsers, 
  FaDatabase, 
  FaCogs, 
  FaBalanceScale, 
  FaServer 
} from 'react-icons/fa';

const WheelContainer = styled.div`
  position: relative;
  width: 800px;
  height: 800px;
  margin: 40px auto;
`;

const CenterCircle = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 200px;
  height: 200px;
  border-radius: 50%;
  background-color: #fff;
  border: 2px solid #2c3e50;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  z-index: 2;
`;

const ComponentCircle = styled.div`
  position: absolute;
  width: 120px;
  height: 120px;
  border-radius: 50%;
  background-color: ${props => props.color};
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  transition: transform 0.3s ease;
  color: white;
  z-index: 2;
`;

const ComponentLabel = styled.div`
  position: absolute;
  width: 200px;
  text-align: center;
  color: #2c3e50;
  font-size: 14px;
  margin-top: 130px;
`;

const ConnectingLine = styled.div`
  position: absolute;
  width: 250px;
  height: 2px;
  background-color: #e0e0e0;
  transform-origin: center;
  z-index: 1;
`;

const components = [
  {
    id: 'teams',
    name: 'Teams & Agents',
    label: 'Teams & Agents Alignment & Structure',
    icon: FaUsers,
    color: '#3498db',
    position: 'top'
  },
  {
    id: 'resources',
    name: 'Resources & Knowledge',
    label: 'Resources & The Corpus of Knowledge',
    icon: FaDatabase,
    color: '#2ecc71',
    position: 'right'
  },
  {
    id: 'actions',
    name: 'Actions & Orchestrations',
    label: 'Actions & Orchestrations Planning',
    icon: FaCogs,
    color: '#e74c3c',
    position: 'bottomRight'
  },
  {
    id: 'compliance',
    name: 'Compliance & Alignment',
    label: 'Compliance, Guardrails & Alignment',
    icon: FaBalanceScale,
    color: '#f39c12',
    position: 'bottomLeft'
  },
  {
    id: 'execution',
    name: 'Execution Infrastructure',
    label: 'Execution Infrastructure & Tech Architecture',
    icon: FaServer,
    color: '#9b59b6',
    position: 'left'
  }
];

const getPosition = (position) => {
  const distance = 300;
  switch (position) {
    case 'top':
      return { left: '50%', top: '10%' };
    case 'right':
      return { left: '85%', top: '50%' };
    case 'bottomRight':
      return { left: '75%', top: '80%' };
    case 'bottomLeft':
      return { left: '25%', top: '80%' };
    case 'left':
      return { left: '15%', top: '50%' };
    default:
      return { left: '50%', top: '50%' };
  }
};

const getLineAngle = (position) => {
  switch (position) {
    case 'top': return '0deg';
    case 'right': return '72deg';
    case 'bottomRight': return '144deg';
    case 'bottomLeft': return '216deg';
    case 'left': return '288deg';
    default: return '0deg';
  }
};

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <CenterCircle>
        <h2>T.R.A.C.E</h2>
        <div>The Intelligence Framework</div>
        <small>Compliance & Alignment</small>
      </CenterCircle>

      {components.map((component) => {
        const position = getPosition(component.position);
        return (
          <React.Fragment key={component.id}>
            <ConnectingLine
              style={{
                left: '50%',
                top: '50%',
                transform: `translateX(-50%) rotate(${getLineAngle(component.position)})`
              }}
            />
            <ComponentCircle
              color={component.color}
              onClick={() => onComponentSelect(component)}
              style={{
                ...position,
                transform: 'translate(-50%, -50%)'
              }}
            >
              <component.icon size={40} />
              <div style={{ fontSize: '12px', marginTop: '8px' }}>
                {component.name}
              </div>
            </ComponentCircle>
            <ComponentLabel
              style={{
                ...position,
                transform: 'translate(-50%, -50%)'
              }}
            >
              {component.label}
            </ComponentLabel>
          </React.Fragment>
        );
      })}
    </WheelContainer>
  );
}

export default FrameworkWheel;
