import React from 'react';
import styled from 'styled-components';
import { 
  FaUsers, 
  FaBrain, 
  FaCogs,
  FaBalanceScale 
} from 'react-icons/fa';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 0 auto;
`;

const WheelContent = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  background-image: url('/inocta-trace.svg');
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
`;

const InteractiveArea = styled.div`
  position: absolute;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: white;
  transition: all 0.3s ease;
  z-index: 2;

  &:hover {
    transform: scale(1.05);
  }

  .icon {
    font-size: 24px;
    margin-bottom: 8px;
  }

  .label {
    font-size: 14px;
    text-align: center;
  }

  &.top {
    top: 40px;
    left: 50%;
    transform: translateX(-50%);
    width: 120px;
    height: 140px;
  }

  &.right {
    right: 40px;
    top: 50%;
    transform: translateY(-50%);
    width: 140px;
    height: 120px;
  }

  &.bottom {
    bottom: 40px;
    left: 50%;
    transform: translateX(-50%);
    width: 120px;
    height: 140px;
  }

  &.left {
    left: 40px;
    top: 50%;
    transform: translateY(-50%);
    width: 140px;
    height: 120px;
  }
`;

const CenterContent = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: #e9a65b;
  cursor: pointer;
  z-index: 2;
  transition: all 0.3s ease;

  &:hover {
    transform: translate(-50%, -50%) scale(1.05);
  }

  .icon {
    font-size: 32px;
    margin-bottom: 8px;
  }

  h3 {
    font-size: 14px;
    margin: 0;
    color: #333;
  }
`;

const sections = [
  { 
    id: 'teams', 
    position: 'top',
    label: 'Teams',
    icon: FaUsers
  },
  { 
    id: 'resources', 
    position: 'right',
    label: 'Resources',
    icon: FaBrain
  },
  { 
    id: 'execution', 
    position: 'bottom',
    label: 'Execution',
    icon: FaCogs
  },
  { 
    id: 'actions', 
    position: 'left',
    label: 'Actions',
    icon: FaCogs
  }
];

const centerSection = {
  id: 'compliance',
  label: 'Compliance & Alignment',
  icon: FaBalanceScale
};

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <WheelContent>
        <CenterContent onClick={() => onComponentSelect(centerSection)}>
          <FaBalanceScale className="icon" />
          <h3>Compliance & Alignment</h3>
        </CenterContent>

        {sections.map((section) => (
          <InteractiveArea
            key={section.id}
            className={section.position}
            onClick={() => onComponentSelect(section)}
          >
            <section.icon className="icon" />
            <div className="label">{section.label}</div>
          </InteractiveArea>
        ))}
      </WheelContent>
    </WheelContainer>
  );
}

export default FrameworkWheel;
