import React from 'react';
import styled from 'styled-components';
import { 
  FaUsers, 
  FaDatabase, 
  FaCogs, 
  FaBalanceScale, 
  FaServer 
} from 'react-icons/fa';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 0 auto;
`;

const WheelContent = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
`;

const ComponentCircle = styled.div`
  position: absolute;
  width: 100px;
  height: 100px;
  border-radius: 50%;
  background-color: ${props => props.color};
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  transition: transform 0.3s ease;
  color: white;
  z-index: 2;

  &:hover {
    transform: translate(-50%, -50%) scale(1.1) !important;
  }
`;

const ComponentLabel = styled.div`
  position: absolute;
  width: 200px;
  text-align: center;
  color: #2c3e50;
  font-size: 14px;
  margin-top: 120px;
`;

const ConnectingLine = styled.div`
  position: absolute;
  width: 150px;
  height: 1px;
  background-color: #2c3e50;
  transform-origin: center;
  z-index: 1;
`;

const components = [
  {
    id: 'teams',
    name: 'Teams & Agents',
    label: 'Teams & Agents Alignment & Structure',
    icon: FaUsers,
    color: '#3498db',
    position: 'top'
  },
  {
    id: 'resources',
    name: 'Resources & Knowledge',
    label: 'Resources & The Corpus of Knowledge',
    icon: FaDatabase,
    color: '#2ecc71',
    position: 'right'
  },
  {
    id: 'actions',
    name: 'Actions & Orchestrations',
    label: 'Actions & Orchestrations Planning',
    icon: FaCogs,
    color: '#e74c3c',
    position: 'bottomRight'
  },
  {
    id: 'compliance',
    name: 'Compliance & Alignment',
    label: 'Compliance, Guardrails & Alignment',
    icon: FaBalanceScale,
    color: '#f39c12',
    position: 'bottomLeft'
  },
  {
    id: 'execution',
    name: 'Execution Infrastructure',
    label: 'Execution Infrastructure & Tech Architecture',
    icon: FaServer,
    color: '#9b59b6',
    position: 'left'
  }
];

const getPosition = (position) => {
  const distance = 180; // Reduced distance for smaller wheel
  const centerOffset = '50%';

  switch (position) {
    case 'top':
      return { 
        left: centerOffset, 
        top: `calc(${centerOffset} - ${distance}px)` 
      };
    case 'right':
      return { 
        left: `calc(${centerOffset} + ${distance}px)`, 
        top: centerOffset 
      };
    case 'bottomRight':
      return { 
        left: `calc(${centerOffset} + ${distance * 0.7}px)`, 
        top: `calc(${centerOffset} + ${distance * 0.7}px)` 
      };
    case 'bottomLeft':
      return { 
        left: `calc(${centerOffset} - ${distance * 0.7}px)`, 
        top: `calc(${centerOffset} + ${distance * 0.7}px)` 
      };
    case 'left':
      return { 
        left: `calc(${centerOffset} - ${distance}px)`, 
        top: centerOffset 
      };
    default:
      return { left: centerOffset, top: centerOffset };
  }
};

const getLineAngle = (position) => {
  switch (position) {
    case 'top': return '0deg';
    case 'right': return '72deg';
    case 'bottomRight': return '144deg';
    case 'bottomLeft': return '216deg';
    case 'left': return '288deg';
    default: return '0deg';
  }
};

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <WheelContent>
        {components.map((component) => {
          const position = getPosition(component.position);
          return (
            <React.Fragment key={component.id}>
              <ConnectingLine
                style={{
                  left: '50%',
                  top: '50%',
                  transform: `translateX(-50%) rotate(${getLineAngle(component.position)})`
                }}
              />
              <ComponentCircle
                color={component.color}
                onClick={() => onComponentSelect(component)}
                style={{
                  ...position,
                  transform: 'translate(-50%, -50%)'
                }}
              >
                <component.icon size={30} />
                <div style={{ fontSize: '10px', marginTop: '5px', textAlign: 'center' }}>
                  {component.name}
                </div>
              </ComponentCircle>
              <ComponentLabel
                style={{
                  ...position,
                  transform: 'translate(-50%, -50%)'
                }}
              >
                {component.label}
              </ComponentLabel>
            </React.Fragment>
          );
        })}
      </WheelContent>
    </WheelContainer>
  );
}

export default FrameworkWheel;
