import React from 'react';
import styled from 'styled-components';
import { 
  FaUsers, 
  FaDatabase, 
  FaCogs, 
  FaBalanceScale, 
  FaServer 
} from 'react-icons/fa';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 40px auto;
`;

const CenterCircle = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 150px;
  height: 150px;
  border-radius: 50%;
  background-color: #fff;
  border: 2px solid #2c3e50;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  z-index: 2;
`;

const ComponentCircle = styled.div`
  position: absolute;
  width: 120px;
  height: 120px;
  border-radius: 50%;
  background-color: ${props => props.color};
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  transition: transform 0.3s ease;
  color: white;

  &:hover {
    transform: scale(1.1);
  }
`;

const ConnectingLine = styled.div`
  position: absolute;
  height: 2px;
  background-color: #e0e0e0;
  transform-origin: 0 0;
  z-index: 1;
`;

const components = [
  {
    id: 'teams',
    name: 'Teams & Agents',
    icon: FaUsers,
    color: '#3498db'
  },
  {
    id: 'resources',
    name: 'Resources & Knowledge',
    icon: FaDatabase,
    color: '#2ecc71'
  },
  {
    id: 'actions',
    name: 'Actions & Orchestrations',
    icon: FaCogs,
    color: '#e74c3c'
  },
  {
    id: 'compliance',
    name: 'Compliance & Alignment',
    icon: FaBalanceScale,
    color: '#f39c12'
  },
  {
    id: 'execution',
    name: 'Execution Infrastructure',
    icon: FaServer,
    color: '#9b59b6'
  }
];

function FrameworkWheel({ onComponentSelect }) {
  const radius = 200; // Distance from center to components

  const calculatePosition = (index, total) => {
    const angle = (index * 2 * Math.PI / total) - Math.PI / 2; // Start from top
    return {
      left: `${50 + Math.cos(angle) * radius/3}%`,
      top: `${50 + Math.sin(angle) * radius/3}%`,
      angle: angle * (180 / Math.PI)
    };
  };

  return (
    <WheelContainer>
      <CenterCircle>
        <div>
          <h3>T.R.A.C.E</h3>
          <small>Compliance & Alignment</small>
        </div>
      </CenterCircle>

      {components.map((component, index) => {
        const position = calculatePosition(index, components.length);
        return (
          <React.Fragment key={component.id}>
            <ConnectingLine
              style={{
                width: `${radius}px`,
                left: '50%',
                top: '50%',
                transform: `rotate(${position.angle}deg)`
              }}
            />
            <ComponentCircle
              color={component.color}
              onClick={() => onComponentSelect(component)}
              style={{
                left: position.left,
                top: position.top,
                transform: 'translate(-50%, -50%)'
              }}
            >
              <component.icon size={40} />
              <div style={{ textAlign: 'center', marginTop: 10, fontSize: '12px' }}>
                {component.name}
              </div>
            </ComponentCircle>
          </React.Fragment>
        );
      })}
    </WheelContainer>
  );
}

export default FrameworkWheel;
