import React from 'react';
import styled from 'styled-components';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 0 auto;
`;

const WheelContent = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const CenterContent = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  z-index: 3;
  width: 150px;

  h3 {
    margin: 8px 0 0;
    font-size: 14px;
    color: #333;
  }
`;

const CircleRing = styled.div`
  position: absolute;
  width: 400px;
  height: 400px;
  border-radius: 50%;
  border: 40px solid #e9a65b;
`;

const TrapezoidSection = styled.div`
  position: absolute;
  width: 120px;
  height: 160px;
  background-color: #51817c;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: white;
  text-align: center;
  padding: 20px;
  z-index: 2;

  &.top {
    top: -80px;
    left: 50%;
    transform: translateX(-50%);
    clip-path: polygon(20% 0%, 80% 0%, 100% 100%, 0% 100%);
  }

  &.right {
    right: -80px;
    top: 50%;
    transform: translateY(-50%) rotate(90deg);
    clip-path: polygon(20% 0%, 80% 0%, 100% 100%, 0% 100%);
  }

  &.bottom {
    bottom: -80px;
    left: 50%;
    transform: translateX(-50%) rotate(180deg);
    clip-path: polygon(20% 0%, 80% 0%, 100% 100%, 0% 100%);
  }

  &.left {
    left: -80px;
    top: 50%;
    transform: translateY(-50%) rotate(270deg);
    clip-path: polygon(20% 0%, 80% 0%, 100% 100%, 0% 100%);
  }

  .icon {
    font-size: 24px;
    margin-bottom: 10px;
  }

  .label {
    font-size: 14px;
    transform: rotate(0deg);
  }
`;

const ConnectingText = styled.div`
  position: absolute;
  color: #e9a65b;
  font-size: 14px;
  font-weight: 500;
  z-index: 1;

  &.policies {
    top: 30%;
    left: 20%;
    transform: rotate(-45deg);
  }

  &.conditions {
    top: 30%;
    right: 20%;
    transform: rotate(45deg);
  }

  &.risks {
    bottom: 30%;
    left: 20%;
    transform: rotate(45deg);
  }

  &.guardrails {
    bottom: 30%;
    right: 20%;
    transform: rotate(-45deg);
  }
`;

const sections = [
  { id: 'teams', name: 'Teams', position: 'top' },
  { id: 'resources', name: 'Resources', position: 'right' },
  { id: 'execution', name: 'Execution', position: 'bottom' },
  { id: 'actions', name: 'Actions', position: 'left' }
];

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <WheelContent>
        <CircleRing />
        <CenterContent>
          <h3>Compliance & Alignment</h3>
        </CenterContent>

        {sections.map((section) => (
          <TrapezoidSection
            key={section.id}
            className={section.position}
            onClick={() => onComponentSelect(section)}
          >
            <div className="label">{section.name}</div>
          </TrapezoidSection>
        ))}

        <ConnectingText className="policies">Policies</ConnectingText>
        <ConnectingText className="conditions">Conditions</ConnectingText>
        <ConnectingText className="risks">Risks</ConnectingText>
        <ConnectingText className="guardrails">Guardrails</ConnectingText>
      </WheelContent>
    </WheelContainer>
  );
}

export default FrameworkWheel;
