import React from 'react';
import styled from 'styled-components';
import { 
  FaUsers, 
  FaBrain, 
  FaCogs,
  FaBalanceScale 
} from 'react-icons/fa';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 0 auto;
`;

const WheelContent = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const CenterContent = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  z-index: 3;
  width: 120px;

  .icon {
    color: #e9a65b;
    font-size: 32px;
    margin-bottom: 8px;
  }

  h3 {
    margin: 8px 0 0;
    font-size: 14px;
    color: #333;
  }
`;

const ConnectingShape = styled.div`
  position: relative;
  width: 400px;
  height: 400px;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border: 30px solid #e9a65b;
    border-radius: 30% 70% 70% 30% / 30% 30% 70% 70%;
    transform: rotate(45deg);
  }
`;

const ComponentSection = styled.div`
  position: absolute;
  width: 100px;
  height: 100px;
  background-color: #51817c;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: white;
  clip-path: polygon(20% 0%, 80% 0%, 100% 100%, 0% 100%);

  .icon {
    font-size: 24px;
    margin-bottom: 5px;
  }

  .label {
    font-size: 12px;
    text-align: center;
  }

  &.top {
    top: -50px;
    left: 50%;
    transform: translateX(-50%);
  }

  &.right {
    right: -50px;
    top: 50%;
    transform: translateY(-50%) rotate(90deg);
  }

  &.bottom {
    bottom: -50px;
    left: 50%;
    transform: translateX(-50%) rotate(180deg);
  }

  &.left {
    left: -50px;
    top: 50%;
    transform: translateY(-50%) rotate(270deg);
  }
`;

const ConnectingText = styled.div`
  position: absolute;
  color: #e9a65b;
  font-size: 14px;
  font-weight: 500;

  &.policies {
    top: 25%;
    left: 15%;
    transform: rotate(-45deg);
  }

  &.conditions {
    top: 25%;
    right: 15%;
    transform: rotate(45deg);
  }

  &.risks {
    bottom: 25%;
    left: 15%;
    transform: rotate(45deg);
  }

  &.guardrails {
    bottom: 25%;
    right: 15%;
    transform: rotate(-45deg);
  }
`;

const components = [
  {
    id: 'teams',
    name: 'Teams',
    icon: FaUsers,
    position: 'top'
  },
  {
    id: 'resources',
    name: 'Resources',
    icon: FaBrain,
    position: 'right'
  },
  {
    id: 'execution',
    name: 'Execution',
    icon: FaCogs,
    position: 'bottom'
  },
  {
    id: 'actions',
    name: 'Actions',
    icon: FaCogs,
    position: 'left'
  }
];

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <WheelContent>
        <ConnectingShape />
        <CenterContent>
          <FaBalanceScale className="icon" />
          <h3>Compliance & Alignment</h3>
        </CenterContent>

        {components.map((component) => (
          <ComponentSection
            key={component.id}
            className={component.position}
            onClick={() => onComponentSelect(component)}
          >
            <component.icon className="icon" />
            <div className="label">{component.name}</div>
          </ComponentSection>
        ))}

        <ConnectingText className="policies">Policies</ConnectingText>
        <ConnectingText className="conditions">Conditions</ConnectingText>
        <ConnectingText className="risks">Risks</ConnectingText>
        <ConnectingText className="guardrails">Guardrails</ConnectingText>
      </WheelContent>
    </WheelContainer>
  );
}

export default FrameworkWheel;
