import React from 'react';
import styled from 'styled-components';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 0 auto;
`;

const WheelContent = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  background-image: url('/inocta-trace.svg');
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
`;

const InteractiveArea = styled.div`
  position: absolute;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: white;
  transition: all 0.3s ease;
  z-index: 2;

  &:hover {
    transform: scale(1.05);
  }

  .icon {
    margin-bottom: 8px;
    width: ${props => (props.id === 'teams' ? '64px' : '32px')};
    height: ${props => (props.id === 'teams' ? '64px' : '32px')};
    display: flex;
    align-items: center;
    justify-content: center;

    img {
      width: 100%;
      height: 100%;
      object-fit: contain;
      filter: brightness(0) invert(1);
    }
  }

  .label {
    font-size: 14px;
    text-align: center;
    color: white;
  }

  &.top {
    top: 60px; /* Adjusted for better alignment */
    left: 50%;
    transform: translateX(-50%);
  }

  &.right {
    right: 80px;
    top: 50%;
    transform: translateY(-50%);
  }

  &.bottom {
    bottom: 80px;
    left: 50%;
    transform: translateX(-50%);
  }

  &.left {
    left: 80px;
    top: 50%;
    transform: translateY(-50%);
  }
`;

const CenterContent = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  cursor: pointer;
  z-index: 2;
  transition: all 0.3s ease;

  &:hover {
    transform: translate(-50%, -50%) scale(1.05);
  }

  .icon {
    margin-bottom: 8px;
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;

    img {
      width: 100%;
      height: 100%;
      object-fit: contain;
      filter: brightness(0) sepia(1) saturate(2) hue-rotate(350deg);
    }
  }

  h3 {
    font-size: 14px;
    margin: 0;
    color: #333;
  }
`;

const sections = [
  { 
    id: 'teams', 
    position: 'top',
    label: 'Teams',
    icon: '/teams.svg'
  },
  { 
    id: 'resources', 
    position: 'right',
    label: 'Resources',
    icon: '/ressources.svg'
  },
  { 
    id: 'execution', 
    position: 'bottom',
    label: 'Execution',
    icon: '/execution.svg'
  },
  { 
    id: 'actions', 
    position: 'left',
    label: 'Actions',
    icon: '/actions.svg'
  }
];

const centerSection = {
  id: 'compliance',
  label: 'Compliance & Alignment',
  icon: '/compliance.svg'
};

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <WheelContent>
        <CenterContent onClick={() => onComponentSelect(centerSection)}>
          <div className="icon">
            <img src={centerSection.icon} alt="Compliance" />
          </div>
          <h3>{centerSection.label}</h3>
        </CenterContent>

        {sections.map((section) => (
          <InteractiveArea
            key={section.id}
            className={section.position}
            id={section.id}
            onClick={() => onComponentSelect(section)}
          >
            <div className="icon">
              <img src={section.icon} alt={section.label} />
            </div>
            <div className="label">{section.label}</div>
          </InteractiveArea>
        ))}
      </WheelContent>
    </WheelContainer>
  );
}

export default FrameworkWheel;
