import React from 'react';
import styled from 'styled-components';
import { 
  FaUsers, 
  FaBrain, 
  FaBalanceScale, 
  FaCogs 
} from 'react-icons/fa';

const WheelContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  margin: 0 auto;
`;

const WheelContent = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const CenterContent = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) rotate(-45deg); // Counter-rotate to keep logo straight
  text-align: center;
  z-index: 3;
  width: 100px;
  height: 100px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  img {
    width: 80%;
    height: 80%;
    object-fit: contain;
  }

  h3 {
    margin: 8px 0 0;
    font-size: 14px;
    color: #333;
  }
`;

const ComponentSquare = styled.div`
  position: relative;
  width: 300px;
  height: 300px;
  border: 2px solid #e9a65b;
  transform: rotate(45deg);
  background: white;
`;

const IconContainer = styled.div`
  position: absolute;
  width: 60px;
  height: 60px;
  background-color: #51817c;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  transform: rotate(-45deg);

  &.top {
    top: -30px;
    left: 50%;
    transform: translate(-50%, 0) rotate(-45deg);
  }

  &.right {
    right: -30px;
    top: 50%;
    transform: translate(0, -50%) rotate(-45deg);
  }

  &.bottom {
    bottom: -30px;
    left: 50%;
    transform: translate(-50%, 0) rotate(-45deg);
  }

  &.left {
    left: -30px;
    top: 50%;
    transform: translate(0, -50%) rotate(-45deg);
  }
`;

const Label = styled.div`
  position: absolute;
  color: #51817c;
  font-size: 14px;
  text-align: center;
  transform: rotate(-45deg);
  width: 100px;

  &.top {
    top: -60px;
    left: 50%;
    transform: translate(-50%, 0);
  }

  &.right {
    right: -80px;
    top: 50%;
    transform: translate(0, -50%);
  }

  &.bottom {
    bottom: -60px;
    left: 50%;
    transform: translate(-50%, 0);
  }

  &.left {
    left: -80px;
    top: 50%;
    transform: translate(0, -50%);
  }
`;

const ConnectingText = styled.div`
  position: absolute;
  color: #e9a65b;
  font-size: 12px;

  &.policies {
    top: 20%;
    left: 0;
  }

  &.conditions {
    top: 20%;
    right: 0;
  }

  &.risks {
    bottom: 20%;
    left: 0;
  }

  &.guardrails {
    bottom: 20%;
    right: 0;
  }
`;

const components = [
  {
    id: 'teams',
    name: 'Teams',
    icon: FaUsers,
    position: 'top'
  },
  {
    id: 'resources',
    name: 'Resources',
    icon: FaBrain,
    position: 'right'
  },
  {
    id: 'execution',
    name: 'Execution',
    icon: FaCogs,
    position: 'bottom'
  },
  {
    id: 'actions',
    name: 'Actions',
    icon: FaCogs,
    position: 'left'
  }
];

function FrameworkWheel({ onComponentSelect }) {
  return (
    <WheelContainer>
      <WheelContent>
        <ComponentSquare>
          <CenterContent>
            <img src="/inocta-logo.svg" alt="Inocta Logo" />
            <h3>Compliance & Alignment</h3>
          </CenterContent>

          {components.map((component) => (
            <React.Fragment key={component.id}>
              <IconContainer 
                className={component.position}
                onClick={() => onComponentSelect(component)}
              >
                <component.icon size={24} />
              </IconContainer>
              <Label className={component.position}>
                {component.name}
              </Label>
            </React.Fragment>
          ))}

          <ConnectingText className="policies">Policies</ConnectingText>
          <ConnectingText className="conditions">Conditions</ConnectingText>
          <ConnectingText className="risks">Risks</ConnectingText>
          <ConnectingText className="guardrails">Guardrails</ConnectingText>
        </ComponentSquare>
      </WheelContent>
    </WheelContainer>
  );
}

export default FrameworkWheel;
